import {makeStyles} from '@material-ui/styles';
const useStyles = makeStyles({
    typeDate: {
        fontFamily: 'Arial',
        fontSize: 14,
        fontWeight: 'normal',
        fontStyle: 'normal',
        fontStretch: 'normal',
        lineHeight: 1.71,
        letterSpacing: '0.14px',
        color: '#666666',
        marginBottom: 7,
    },
    title: {
        fontFamily: 'DinProMedium',
        fontSize: 16,
        fontWeight: 500,
        fontStyle: 'normal',
        fontStretch: 'normal',
        lineHeight: 1.5,
        letterSpacing: 'normal',
        color: '#151515',
    },
    copy: {
        fontFamily: 'Arial',
        fontSize: 14,
        fontWeight: 'normal',
        fontStyle: 'normal',
        fontStretch: 'normal',
        lineHeight: 1.71,
        letterSpacing: '.14px',
        color: '#333333',
    },
    image: {
        width: '100%',
    },
});

export default useStyles;
